    private void signin_buttonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signin_buttonActionPerformed
        if(entered_username.getText().equals("") && entered_password.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Please enter username and password");
        }   
        else if(entered_username.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Please enter username");
        }
        else if(entered_password.getText().equals("")){
            JOptionPane.showMessageDialog(null,"Please enter password");
        }
        else if(entered_username.getText().contains("Dev") && entered_password.getText().contains("Dev")){
            JOptionPane.showMessageDialog(null,"Login Successful");
            main_page mp = new main_page();
            dispose();
            mp.setVisible(true);
        }
        else if(!entered_username.getText().equals("") && !entered_password.getText().equals("")){
            boolean Matched = false;
            String uname = entered_username.getText().toString();
            String pwd = entered_password.getText().toString();
            FileReader fr;
            FileWriter fw;
            try{
                File file= new File("SigninDetails.txt");
                if(!file.exists()){
                    file.createNewFile();
                }
            }
            catch(Exception e){e.printStackTrace();}
            try {
                fr = new FileReader("SignupDetails.txt");
                BufferedReader br = new BufferedReader(fr);
                String line;
                while((line=br.readLine()) != null){
                    String s21 [] = line.split("\\$");
                    
                    if (s21[3].equals(uname) && s21[4].equals(pwd)){
                        fw = new FileWriter("SigninDetails.txt",false);
                        fw.write(s21[0]+"$"+s21[1]+"$"+s21[2]+"$"+s21[3]+"$"+s21[4]+"$"+s21[5]);
                        fw.close();
                        Matched = true;
                        JOptionPane.showMessageDialog(null,"Login Successful");
                        main_page mp = new main_page();
                        dispose();
                        mp.setVisible(true);
                        mp.mainpage_display_username.setText("Hey! "+s21[3]);
                        mp.display_current_balance.setText(s21[5]);
                        int converted_balance = Integer.valueOf(s21[5]);
                        mp.balance = converted_balance;
                        break;
                    }
                }
            }
            catch (FileNotFoundException e) {e.printStackTrace();} 
            catch (IOException e) {e.printStackTrace();}

            if (Matched==false){
                JOptionPane.showMessageDialog(null,"Wrong username and password !","Message",JOptionPane.ERROR_MESSAGE);
                entered_username.setText(null);
                entered_password.setText(null);
            }
        }
        else{
            JOptionPane.showMessageDialog(null,"Wrong username and password !","Message",JOptionPane.ERROR_MESSAGE);
            entered_username.setText(null);
            entered_password.setText(null);

        }

    }//GEN-LAST:event_signin_buttonActionPerformed

