    private void signin_showpass_bottonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_signin_showpass_bottonActionPerformed
        if(signin_showpass_botton.isSelected()){
            entered_password.setEchoChar((char)0);
        }
        else{
            entered_password.setEchoChar('*');
        }
    }//GEN-LAST:event_signin_showpass_bottonActionPerformed

